import React, { useState, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Box, TextField, Pagination, CircularProgress } from "@mui/material";
import { fetchMovies } from "../reducer/moviesSlice"; // Assuming you have the fetchMovies action
import MovieCard from "./MovieCard"; // MovieCard component

const SearchBar = () => {
  const [query, setQuery] = useState("");
  const [page, setPage] = useState(1); // Track current page
  const dispatch = useDispatch();
  const { movies, totalPages, loading, error } = useSelector(
    (state) => state.movies
  );

  // Fetch movies based on query and page
  const handleSearch = () => {
    if (query) {
      dispatch(fetchMovies({ query, page }));
    }
  };

  // Handle page change
  const handlePageChange = (event, value) => {
    setPage(value); // Update the page state
  };

  useEffect(() => {
    if (query) {
      handleSearch();
    }
  }, [query, page]); // Fetch movies whenever the query or page changes

  return (
    <Box>
      <TextField
        label="Search Movies"
        variant="outlined"
        value={query}
        onChange={(e) => setQuery(e.target.value)} // Update query state
        fullWidth
        sx={{ marginBottom: 2 }}
      />

      {loading ? (
        <CircularProgress sx={{ display: "block", margin: "0 auto" }} />
      ) : (
        <Box>
          {error && <p>{error}</p>} {/* Display error if any */}
          <Box
            sx={{ display: "flex", flexWrap: "wrap", justifyContent: "center" }}
          >
            {movies.map((movie) => (
              <MovieCard key={movie.id} movie={movie} />
            ))}
          </Box>
          <Pagination
            count={totalPages}
            page={page}
            onChange={handlePageChange}
            color="primary"
            sx={{ display: "flex", justifyContent: "center", marginTop: 2 }}
          />
        </Box>
      )}
    </Box>
  );
};

export default SearchBar;
