import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import {
  Card,
  CardMedia,
  CardContent,
  Typography,
  Button,
  Box,
} from "@mui/material";

const MovieDetails = () => {
  const { id } = useParams();
  const [movieDetails, setMovieDetails] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMovieDetails = async () => {
      const API_URL = "https://api.themoviedb.org/3/movie";
      const AUTH_TOKEN =
        "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiJiZmRhNWNlMmU5NjQ4YTY3ZGJmZDM3YzBkYTQ2MDY3YSIsIm5iZiI6MTczMjc3NzkyMi43MTc5NDE1LCJzdWIiOiI2NzQ4MTYwNDFkMGI1YjNmYjgzM2YyMTYiLCJzY29wZXMiOlsiYXBpX3JlYWQiXSwidmVyc2lvbiI6MX0.rOIeLQi9K6HjHcEVAsbWBxvj9DdNz-y2DvUXpssEjGs";

      try {
        const response = await fetch(`${API_URL}/${id}?language=en-US`, {
          method: "GET",
          headers: {
            accept: "application/json",
            Authorization: AUTH_TOKEN,
          },
        });
        const data = await response.json();
        setMovieDetails(data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching movie details:", error);
        setLoading(false);
      }
    };

    fetchMovieDetails();
  }, [id]); // Re-fetch when ID changes

  const hasPoster =
    movieDetails?.poster_path !== null && movieDetails?.poster_path !== "";
  const placeholderImage =
    "https://via.placeholder.com/300x450?text=No+Image+Available";

  if (loading)
    return <CircularProgress sx={{ display: "block", margin: "0 auto" }} />;

  if (!movieDetails) return <div>Movie not found</div>;

  return (
    <Box sx={{ padding: 3 }}>
      <Button
        component={Link}
        to="/"
        variant="contained"
        color="primary"
        sx={{ marginBottom: 2 }}
      >
        Back to Search
      </Button>

      <Card sx={{ display: "flex", boxShadow: 3 }}>
        <CardMedia
          component="img"
          sx={{ width: 300, objectFit: "cover" }}
          image={
            hasPoster
              ? `https://image.tmdb.org/t/p/w500/${movieDetails.poster_path}`
              : placeholderImage
          }
          alt={movieDetails?.title}
        />
        <CardContent>
          <Typography variant="h4" component="div" gutterBottom>
            {movieDetails?.title || "N/A"}
          </Typography>
          <Typography variant="body1" color="text.secondary" paragraph>
            <strong>Overview:</strong>{" "}
            {movieDetails?.overview || "No description available."}
          </Typography>
          <Typography variant="body2" color="text.secondary" paragraph>
            <strong>Release Date:</strong>{" "}
            {movieDetails.release_date || "Release date not available"}
          </Typography>
          <Typography variant="body2" color="text.secondary" paragraph>
            <strong>Rating:</strong> {movieDetails?.vote_average} / 10
          </Typography>
          <Typography variant="body2" color="text.secondary" paragraph>
            <strong>Genres:</strong>{" "}
            {movieDetails?.genres?.map((genre) => genre?.name).join(", ")}
          </Typography>
        </CardContent>
      </Card>
    </Box>
  );
};

export default MovieDetails;
