import React, { useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import {
  Card,
  CardMedia,
  CardContent,
  Typography,
  Button,
  Box,
  CircularProgress,
  Grid,
} from "@mui/material";
import { fetchMovieDetails } from "../reducer/movieDetailsSlice";
import { fetchMovieReviews } from "../reducer/reviewsSlice"; // Import the reviews fetch action
import Reviews from "./Reviews"; // Import the Reviews component

const MovieDetailsPage = () => {
  const { id } = useParams(); // Get movie ID from URL
  const dispatch = useDispatch();
  const navigate = useNavigate(); // Use navigate hook

  // Get movie details and reviews from Redux state
  const {
    movie,
    loading: movieLoading,
    error: movieError,
  } = useSelector((state) => state.movieDetails);
  const {
    reviews,
    loading: reviewsLoading,
    error: reviewsError,
  } = useSelector((state) => state.reviews);

  useEffect(() => {
    dispatch(fetchMovieDetails(id)); // Fetch movie details
    dispatch(fetchMovieReviews(id)); // Fetch movie reviews
  }, [id, dispatch]);

  if (movieLoading || reviewsLoading)
    return (
      <CircularProgress
        sx={{ display: "block", margin: "0 auto", marginTop: "50px" }}
      />
    );
  if (movieError) return <div>{movieError}</div>;
  if (reviewsError) return <div>{reviewsError}</div>;
  if (!movie) return <div>Movie not found</div>;

  const placeholderImage =
    "https://via.placeholder.com/300x450?text=No+Image+Available";

  const hasPoster = movie.poster_path !== null && movie.poster_path !== "";

  const handleBackClick = () => {
    navigate("/"); // Navigate back to the search page
  };

  return (
    <Box>
      <Button
        onClick={handleBackClick}
        variant="contained"
        color="primary"
        sx={{ marginBottom: 2 }}
      >
        Back to Search
      </Button>

      {/* Responsive Movie Details Layout */}
      <Grid
        container
        spacing={3}
        sx={{
          flexDirection: { xs: "column", sm: "column", md: "row" }, // Column for mobile/tablets, row for desktops
        }}
      >
        {/* Movie Poster */}
        <Grid item xs={12} md={4}>
          <CardMedia
            component="img"
            sx={{
              width: "100%",
              objectFit: "cover",
              borderRadius: 2,
              boxShadow: 2,
              height: { xs: 300, sm: 400, md: 450 }, // Dynamic height for image
            }}
            image={
              hasPoster
                ? `https://image.tmdb.org/t/p/w500/${movie.poster_path}`
                : placeholderImage
            }
            alt={movie.title || "No Title Available"}
          />
        </Grid>

        {/* Movie Details */}
        <Grid item xs={12} md={8}>
          <CardContent>
            <Typography variant="h4" component="div" gutterBottom>
              {movie.title}
            </Typography>
            <Typography variant="body1" color="text.secondary" paragraph>
              <strong>Overview:</strong>{" "}
              {movie?.overview || "No description available."}
            </Typography>
            <Typography variant="body2" color="text.secondary" paragraph>
              <strong>Release Date:</strong> {movie?.release_date || "N/A"}
            </Typography>
            <Typography variant="body2" color="text.secondary" paragraph>
              <strong>Rating:</strong> {movie?.vote_average} / 10
            </Typography>
            <Typography variant="body2" color="text.secondary" paragraph>
              <strong>Genres:</strong>{" "}
              {movie?.genres?.map((genre) => genre?.name)?.join(", ")}
            </Typography>
          </CardContent>
        </Grid>
      </Grid>

      <Grid
        container
        spacing={3}
        sx={{
          marginTop: { xs: 2, sm: 0, md: 0 }, // Responsive top margin
          paddingX: { xs: 2, sm: 0, md: 0 }, // Responsive left/right padding
          paddingTop: 2,
        }}
      >
        <Grid item xs={12}>
          {/* Centered Border */}
          <Box
            sx={{
              width: "100%", // Adjust width for responsiveness
              borderTop: "2px solid #ccc", // Border style
              margin: "0 auto", // Center the border
            }}
          />
          {/* Title */}
          <Typography
            variant="h5"
            sx={{
              marginTop: 2,
              fontWeight: "bold",
              textAlign: "center",
            }}
          >
            Reviews
          </Typography>
          {/* Reviews Component */}
          <Reviews reviews={reviews} />
        </Grid>
      </Grid>
    </Box>
  );
};

export default MovieDetailsPage;
