import React, { useEffect } from "react";
import { useParams, Link } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import {
  Card,
  CardMedia,
  CardContent,
  Typography,
  Button,
  Box,
  CircularProgress,
} from "@mui/material";
import { fetchMovieDetails } from "../reducer/movieDetailsSlice";

const MovieDetailsPage = () => {
  const { id } = useParams(); // Get movie ID from URL
  const dispatch = useDispatch();

  // Get movie details from Redux state
  const { movie, loading, error } = useSelector((state) => state.movieDetails);

  useEffect(() => {
    dispatch(fetchMovieDetails(id)); // Fetch movie details when component mounts
  }, [id, dispatch]);

  if (loading)
    return (
      <CircularProgress
        sx={{ display: "block", margin: "0 auto", marginTop: "50px" }}
      />
    );
  if (error) return <div>{error}</div>;
  if (!movie) return <div>Movie not found</div>;

  const placeholderImage =
    "https://via.placeholder.com/300x450?text=No+Image+Available";

  const hasPoster = movie.poster_path !== null && movie.poster_path !== "";

  return (
    <Box sx={{ padding: 3 }}>
      <Button
        component={Link}
        to="/"
        variant="contained"
        color="primary"
        sx={{ marginBottom: 2 }}
      >
        Back to Search
      </Button>

      <Card sx={{ display: "flex", boxShadow: 3 }}>
        <CardMedia
          component="img"
          sx={{ width: 300, objectFit: "fill" }}
          image={
            hasPoster
              ? `https://image.tmdb.org/t/p/w500/${movie.poster_path}`
              : placeholderImage
          }
          alt={movie.title || "No Title Available"}
        />
        <CardContent>
          <Typography variant="h4" component="div" gutterBottom>
            {movie.title}
          </Typography>
          <Typography variant="body1" color="text.secondary" paragraph>
            <strong>Overview:</strong>{" "}
            {movie?.overview || "No description available."}
          </Typography>
          <Typography variant="body2" color="text.secondary" paragraph>
            <strong>Release Date:</strong> {movie?.release_date}
          </Typography>
          <Typography variant="body2" color="text.secondary" paragraph>
            <strong>Rating:</strong> {movie?.vote_average} / 10
          </Typography>
          <Typography variant="body2" color="text.secondary" paragraph>
            <strong>Genres:</strong>{" "}
            {movie?.genres?.map((genre) => genre?.name)?.join(", ")}
          </Typography>
        </CardContent>
      </Card>
    </Box>
  );
};

export default MovieDetailsPage;
