import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

const API_URL = "https://api.themoviedb.org/3/search/movie";
const AUTH_TOKEN =
  "Bearer eyJhbGciOiJIUzI1NiJ9.eyJhdWQiOiJiZmRhNWNlMmU5NjQ4YTY3ZGJmZDM3YzBkYTQ2MDY3YSIsIm5iZiI6MTczMjc3NzkyMi43MTc5NDE1LCJzdWIiOiI2NzQ4MTYwNDFkMGI1YjNmYjgzM2YyMTYiLCJzY29wZXMiOlsiYXBpX3JlYWQiXSwidmVyc2lvbiI6MX0.rOIeLQi9K6HjHcEVAsbWBxvj9DdNz-y2DvUXpssEjGs";

export const fetchMovies = createAsyncThunk(
  "movies/fetchMovies",
  async (query, { rejectWithValue }) => {
    try {
      const response = await fetch(
        `${API_URL}?query=${query}&include_adult=false&language=en-US&page=1`,
        {
          method: "GET",
          headers: {
            accept: "application/json",
            Authorization: AUTH_TOKEN,
          },
        }
      );
      if (!response.ok) {
        throw new Error("Failed to fetch movies");
      }
      const data = await response.json();
      return data.results;
    } catch (error) {
      return rejectWithValue(error.message);
    }
  }
);

const moviesSlice = createSlice({
  name: "movies",
  initialState: {
    movies: [],
    loading: false,
    error: null,
  },
  reducers: {},
  extraReducers: (builder) => {
    builder
      .addCase(fetchMovies.pending, (state) => {
        state.loading = true;
        state.error = null;
      })
      .addCase(fetchMovies.fulfilled, (state, action) => {
        state.loading = false;
        state.movies = action.payload;
      })
      .addCase(fetchMovies.rejected, (state, action) => {
        state.loading = false;
        state.error = action.payload;
      });
  },
});

export default moviesSlice.reducer;
