import React, { useState, useCallback, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import { Box, TextField, Pagination, CircularProgress } from "@mui/material";
import { fetchMovies } from "../reducer/moviesSlice"; // Assuming you have the fetchMovies action
import MovieCard from "./MovieCard"; // MovieCard component
import debounce from "lodash.debounce"; // Import lodash debounce

const SearchBar = () => {
  const [query, setQuery] = useState(""); // To store search query
  const [page, setPage] = useState(1); // To track current page
  const dispatch = useDispatch();
  const { movies, totalPages, loading, error } = useSelector(
    (state) => state.movies
  );

  // Create a debounced version of the fetchMovies function
  const debouncedSearch = useCallback(
    debounce((searchQuery, page) => {
      if (searchQuery.trim()) {
        dispatch(fetchMovies({ query: searchQuery, page }));
      } else {
        // If query is empty, clear any previous search results (optional)
        dispatch(fetchMovies({ query: "", page }));
      }
    }, 500), // Debounce delay of 1000ms
    []
  );

  // Handle the search input change
  const handleInputChange = (e) => {
    const newQuery = e.target.value;
    setQuery(newQuery); // Update query state
    debouncedSearch(newQuery, page); // Call the debounced function with current page
  };

  // Handle page change
  const handlePageChange = (event, value) => {
    setPage(value); // Update page state
    debouncedSearch(query, value); // Trigger search with the updated page
  };

  useEffect(() => {
    if (query.trim()) {
      debouncedSearch(query, page); // Call debounced function if there is a query
    }
  }, [page, query, debouncedSearch]);

  // Clean up the debounced function on component unmount
  useEffect(() => {
    return () => {
      debouncedSearch.cancel();
    };
  }, [debouncedSearch]);

  return (
    <Box>
      <TextField
        label="Search Movies"
        variant="outlined"
        value={query}
        onChange={handleInputChange} // Update query on change
        fullWidth
        sx={{ marginBottom: 2 }}
      />

      {loading ? (
        <CircularProgress sx={{ display: "block", margin: "0 auto" }} />
      ) : (
        <Box>
          {error && <p>{error}</p>} {/* Show error if any */}
          <Box
            sx={{ display: "flex", flexWrap: "wrap", justifyContent: "center" }}
          >
            {/* {movies?.map((movie) => ( */}
            <MovieCard key={movie?.id} movie={movie} />
            {/* ))} */}
          </Box>
          {/* Pagination */}
          <Pagination
            count={totalPages}
            page={page}
            onChange={handlePageChange}
            color="primary"
            sx={{ display: "flex", justifyContent: "center", marginTop: 2 }}
          />
        </Box>
      )}
    </Box>
  );
};

export default SearchBar;
