import React, { useState, useCallback, useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";
import {
  Box,
  TextField,
  Pagination,
  CircularProgress,
  Typography,
} from "@mui/material";
import { fetchMovies } from "../reducer/moviesSlice"; // Assuming you have the fetchMovies action
import MovieCard from "./MovieCard"; // MovieCard component
import debounce from "lodash.debounce"; // Import lodash debounce
import { Link } from "react-router-dom";

const SearchBar = () => {
  const [query, setQuery] = useState(""); // To store search query
  const [page, setPage] = useState(1); // To track current page
  const dispatch = useDispatch();
  const { movies, totalPages, loading, error } = useSelector(
    (state) => state.movies
  );

  // Create a debounced version of the fetchMovies function for query changes
  const debouncedSearchQuery = useCallback(
    debounce((searchQuery, page) => {
      if (searchQuery.trim()) {
        dispatch(fetchMovies({ query: searchQuery, page }));
      }
      //  else {
      //   dispatch(fetchMovies({ query: "", page: 1 })); // Reset results if query is empty
      // }
    }, 500), // Debounce delay of 500ms
    []
  );

  // Create a debounced version of the fetchMovies function for page changes
  const debouncedSearchPage = useCallback(
    debounce((page) => {
      dispatch(fetchMovies({ query, page }));
    }, 500),
    [query]
  );

  console.log(query);

  // Handle the search input change
  const handleInputChange = (e) => {
    const newQuery = e.target.value;
    setQuery(newQuery); // Update query state

    // Only trigger debounced search if input is not just spaces or empty
    if (newQuery.trim() !== "") {
      debouncedSearchQuery(newQuery, page); // Trigger search with debounced function for query change
    } else {
      dispatch(fetchMovies({ query: "", page: 1 })); // Reset search if input is empty or spaces
    }
  };

  // Handle page change
  const handlePageChange = (event, value) => {
    setPage(value); // Update page state
    debouncedSearchPage(value); // Trigger search with debounced function for page change
  };

  // Trigger the search when page or query changes
  useEffect(() => {
    if (query.trim()) {
      debouncedSearchQuery(query, page); // Call debounced function if there is a query
    }
  }, [page, query, debouncedSearchQuery]);

  // Clean up the debounced function on component unmount
  useEffect(() => {
    return () => {
      debouncedSearchQuery.cancel();
      debouncedSearchPage.cancel();
    };
  }, [debouncedSearchQuery, debouncedSearchPage]);

  return (
    <Box
      display={"flex"}
      justifyContent={"center"}
      flexDirection={"column"}
      alignItems={"center"}
    >
      <TextField
        type="search"
        label="Search Movies"
        variant="outlined"
        value={query}
        onChange={handleInputChange} // Update query on change
        fullWidth
        sx={{
          marginBottom: 2,
          maxWidth: 400, // Set max width for input box
        }}
      />

      {loading ? (
        <CircularProgress sx={{ display: "block", margin: "0 auto" }} />
      ) : (
        <Box>
          {error && <p>{error}</p>} {/* Show error if any */}
          {/* If no movies found, show a "No Movies Found" message */}
          {movies?.length === 0 ? (
            <Typography variant="h6" sx={{ textAlign: "center", marginTop: 2 }}>
              No movies found.
            </Typography>
          ) : (
            <Box
              sx={{
                display: "flex",
                flexWrap: "wrap",
                justifyContent: "center",
                gap: 2, // Optional: add spacing between movie cards
              }}
            >
              {movies?.map((movie) => (
                <Link to={`/${movie.id}`}>
                  {" "}
                  <MovieCard key={movie?.id} movie={movie} />
                </Link>
              ))}
            </Box>
          )}
          {/* Pagination */}
          {movies.length > 0 && (
            <Pagination
              count={totalPages}
              page={page}
              onChange={handlePageChange}
              color="primary"
              sx={{
                display: "flex",
                justifyContent: "center",
                marginTop: 5,
                marginBottom: 5,
              }}
            />
          )}
        </Box>
      )}
    </Box>
  );
};

export default SearchBar;
