import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";

export const fetchMovies = createAsyncThunk(
  "movies/fetchMovies",
  async (query, { getState, dispatch }) => {
    const { page } = getState().movies;
    const url = `https://api.themoviedb.org/3/search/movie?query=${query}&page=${page}&include_adult=false&language=en-US`;

    const response = await fetch(url, {
      headers: {
        accept: "application/json",
        Authorization: "Bearer YOUR_API_KEY", // Replace with your actual API key
      },
    });
    const data = await response.json();
    return data.results;
  }
);

const moviesSlice = createSlice({
  name: "movies",
  initialState: {
    results: [],
    page: 1,
    loading: false,
    error: null,
  },
  reducers: {
    setPage: (state, action) => {
      state.page = action.payload;
    },
  },
  extraReducers: (builder) => {
    builder
      .addCase(fetchMovies.pending, (state) => {
        state.loading = true;
      })
      .addCase(fetchMovies.fulfilled, (state, action) => {
        state.loading = false;
        state.results = [...state.results, ...action.payload];
        state.page += 1; // Increment the page for next request
      })
      .addCase(fetchMovies.rejected, (state, action) => {
        state.loading = false;
        state.error = action.error.message;
      });
  },
});

export const { setPage } = moviesSlice.actions;
export default moviesSlice.reducer;
