import React from "react";
import { Card, CardMedia, CardContent, Typography } from "@mui/material";

const MovieCard = ({ movie }) => {
  const placeholderImage =
    "https://via.placeholder.com/300x450?text=No+Image+Available";

  const hasPoster = movie.poster_path !== null && movie.poster_path !== "";

  return (
    <Card sx={{ width: 300, height: 500, margin: 2, boxShadow: 3 }}>
      <CardMedia
        component="img"
        height="450"
        sx={{
          height: 350, // Fixed height for the image
          objectFit: "cover", // Ensures the image fills the area without stretching
        }}
        image={
          hasPoster
            ? `https://image.tmdb.org/t/p/w500/${movie.poster_path}`
            : placeholderImage
        }
        alt={movie.title || "No Title Available"}
      />

      <CardContent>
        <Typography variant="h6" component="div">
          {movie.title || "No Title Available"}
        </Typography>

        <Typography variant="body2" color="text.secondary">
          {movie.release_date
            ? `Release Date: ${movie.release_date}`
            : "Release Date: N/A"}
        </Typography>

        {/* Overview with fallback */}
        <Typography variant="body2" color="text.secondary">
          {movie.overview
            ? movie.overview.length > 100
              ? `${movie.overview.substring(0, 100)}...`
              : movie.overview
            : "No description available."}
        </Typography>
      </CardContent>
    </Card>
  );
};

export default MovieCard;
