import React, { useEffect } from "react";
import { useNavigate, useParams } from "react-router-dom";
import { useDispatch, useSelector } from "react-redux";
import {
  Card,
  CardMedia,
  CardContent,
  Typography,
  Button,
  Box,
  CircularProgress,
  Grid,
} from "@mui/material";
import { fetchMovieDetails } from "../reducer/movieDetailsSlice";
import { fetchMovieReviews } from "../reducer/reviewsSlice"; // Import the reviews fetch action
import Reviews from "./Reviews"; // Import the Reviews component

const MovieDetailsPage = () => {
  const { id } = useParams(); // Get movie ID from URL
  const dispatch = useDispatch();
  const navigate = useNavigate(); // Use navigate hook

  // Get movie details and reviews from Redux state
  const {
    movie,
    loading: movieLoading,
    error: movieError,
  } = useSelector((state) => state.movieDetails);
  const {
    reviews,
    loading: reviewsLoading,
    error: reviewsError,
  } = useSelector((state) => state.reviews);

  useEffect(() => {
    dispatch(fetchMovieDetails(id)); // Fetch movie details
    dispatch(fetchMovieReviews(id)); // Fetch movie reviews
  }, [id, dispatch]);

  if (movieLoading || reviewsLoading)
    return (
      <CircularProgress
        sx={{ display: "block", margin: "0 auto", marginTop: "50px" }}
      />
    );
  if (movieError) return <div>{movieError}</div>;
  if (reviewsError) return <div>{reviewsError}</div>;
  if (!movie) return <div>Movie not found</div>;

  const placeholderImage =
    "https://via.placeholder.com/300x450?text=No+Image+Available";

  const hasPoster = movie.poster_path !== null && movie.poster_path !== "";

  const handleBackClick = () => {
    navigate("/"); // Navigate back to the search page
  };

  return (
    <Box sx={{ padding: 3 }}>
      <Button
        onClick={handleBackClick}
        variant="contained"
        color="primary"
        sx={{ marginBottom: 2 }}
      >
        Back to Search
      </Button>

      {/* Movie Details Layout */}
      <Grid container spacing={3}>
        {/* Movie Details Card */}
        <Grid item xs={12} sm={6} md={4}>
          <Card sx={{ display: "flex", boxShadow: 3 }}>
            <CardMedia
              component="img"
              sx={{
                width: "100%",
                objectFit: "cover",
                height: { xs: 300, sm: 400, md: 450 }, // Responsive height for images
              }}
              image={
                hasPoster
                  ? `https://image.tmdb.org/t/p/w500/${movie.poster_path}`
                  : placeholderImage
              }
              alt={movie.title || "No Title Available"}
            />
            <CardContent>
              <Typography variant="h4" component="div" gutterBottom>
                {movie.title}
              </Typography>
              <Typography variant="body1" color="text.secondary" paragraph>
                <strong>Overview:</strong>{" "}
                {movie?.overview || "No description available."}
              </Typography>
              <Typography variant="body2" color="text.secondary" paragraph>
                <strong>Release Date:</strong> {movie?.release_date}
              </Typography>
              <Typography variant="body2" color="text.secondary" paragraph>
                <strong>Rating:</strong> {movie?.vote_average} / 10
              </Typography>
              <Typography variant="body2" color="text.secondary" paragraph>
                <strong>Genres:</strong>{" "}
                {movie?.genres?.map((genre) => genre?.name)?.join(", ")}
              </Typography>
            </CardContent>
          </Card>
        </Grid>

        {/* Movie Details (Text and Overview) */}
        <Grid item xs={12} sm={6} md={8}>
          <Box sx={{ marginLeft: { xs: 0, sm: 3 }, marginTop: 2 }}>
            <Typography variant="h6" color="text.primary" paragraph>
              <strong>Overview:</strong>
            </Typography>
            <Typography variant="body1" color="text.secondary" paragraph>
              {movie?.overview || "No description available."}
            </Typography>

            <Typography variant="body2" color="text.secondary" paragraph>
              <strong>Release Date:</strong> {movie?.release_date}
            </Typography>

            <Typography variant="body2" color="text.secondary" paragraph>
              <strong>Rating:</strong> {movie?.vote_average} / 10
            </Typography>

            <Typography variant="body2" color="text.secondary" paragraph>
              <strong>Genres:</strong>{" "}
              {movie?.genres?.map((genre) => genre?.name)?.join(", ")}
            </Typography>
          </Box>
        </Grid>
      </Grid>

      {/* Reviews Section */}
      <Grid container spacing={3} sx={{ marginTop: 4 }}>
        <Grid item xs={12}>
          <Reviews reviews={reviews} />
        </Grid>
      </Grid>
    </Box>
  );
};

export default MovieDetailsPage;
