import React, { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";
import {
  Card,
  CardMedia,
  CardContent,
  Typography,
  Button,
  Box,
} from "@mui/material";

const MovieDetails = () => {
  const { id } = useParams(); // Get movie ID from URL
  const [movieDetails, setMovieDetails] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchMovieDetails = async () => {
      try {
        const response = await fetch(
          `https://api.themoviedb.org/3/movie/${id}?api_key=YOUR_API_KEY&language=en-US`
        );
        const data = await response.json();
        setMovieDetails(data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching movie details:", error);
        setLoading(false);
      }
    };

    fetchMovieDetails();
  }, [id]); // Re-fetch when ID changes

  if (loading) return <div>Loading...</div>;

  if (!movieDetails) return <div>Movie not found</div>;

  return (
    <Box sx={{ padding: 3 }}>
      <Button
        component={Link}
        to="/"
        variant="contained"
        color="primary"
        sx={{ marginBottom: 2 }}
      >
        Back to Search
      </Button>

      <Card sx={{ display: "flex", boxShadow: 3 }}>
        <CardMedia
          component="img"
          sx={{ width: 300, objectFit: "cover" }}
          image={`https://image.tmdb.org/t/p/w500/${movieDetails?.poster_path}`}
          alt={movieDetails?.title}
        />
        <CardContent>
          <Typography variant="h4" component="div" gutterBottom>
            {movieDetails?.title}
          </Typography>
          <Typography variant="body1" color="text.secondary" paragraph>
            <strong>Overview:</strong>{" "}
            {movieDetails?.overview || "No description available."}
          </Typography>
          <Typography variant="body2" color="text.secondary" paragraph>
            <strong>Release Date:</strong> {movieDetails.release_date}
          </Typography>
          <Typography variant="body2" color="text.secondary" paragraph>
            <strong>Rating:</strong> {movieDetails?.vote_average} / 10
          </Typography>
          <Typography variant="body2" color="text.secondary" paragraph>
            <strong>Genres:</strong>{" "}
            {movieDetails?.genres?.map((genre) => genre?.name).join(", ")}
          </Typography>
        </CardContent>
      </Card>
    </Box>
  );
};

export default MovieDetails;
